const data = {
    dataLists: [
        {
            Id: Math.random(),
            Title: "Water Bottles",
            Quantity: "20",
            Description: "Minaral, Normal, filter",
            completed: false,
        },
        {
            Id: Math.random(),
            Title: "Juice Bottles",
            Quantity: "50",
            Description: "Apple, orange, grapes, Pineaple",
            completed: true,
        },
        {
            Id: Math.random(),
            Title: "Oil container",
            Quantity: "50",
            Description: "eatable, petrol",
            completed: false,
        }
    ],
};

export default function rootReducer(state = data, action) {
    switch (action.type) {
        case 'ADD_TODO':
            return {
                ...state,
                dataLists: [...state.dataLists, action.data]
            }
        case 'REMOVE_TODO':
            const filteredList = state.dataLists.filter(item => item.Id !== action.id);
            return {
                ...state,
                dataLists: filteredList,
            }
        case 'UPDATE_TODO':
            const { dataLists } = state;
            const target = dataLists.filter(item => item.Id === action.data.Id)[0];
            const index = dataLists.indexOf(target);
            console.log(index, 'checkkkk')
            dataLists[index].Id = action.data.Id;
            dataLists[index].Description = action.data.Description;
            dataLists[index].Title = action.data.Title;
            dataLists[index].Quantity = action.data.Quantity;
            // for (let i = 0; i > dataLists.length; i++) {
            //     console.log(dataLists[i].Id, 'id');
            //     if (dataLists[i].Id === action.data.Id) {
            //         dataLists[i].Id = action.data.Id;
            //         dataLists[i].Description = action.data.Description;
            //         dataLists[i].Title = action.data.Title;
            //         dataLists[i].Quantity = action.data.Quantity;
            //         break;
            //     }
            // }
            console.log(dataLists, 'checking');
            return {
                ...state,
                dataLists: [...state.dataLists],
            }
        default:
            return state
    }
}
